import { useState } from "react";
import "./style.css"

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];

function App() {
  const [showState,changeState] = useState(false)
   return(
    <>
    <nav>
    <div className="logo">
    <div><pre><img src="logo.png"></img>TODAY I LEARNED</pre></div>
    <button className="btn1" onClick={()=> changeState((show)=>!show)}>SHARE A FACT</button>
    </div>
    {showState?<FactForm/>:null}
    </nav>

    <div className="main">     
    <Options/>
    <Facts/>
    </div>

    </>)
}

fun

function FactForm(){

  return(
            <div className="submit">
            <form className="fact-form">
                <input type="text" placeholder="Share a fact with the world..." value=""></input>
                <span>200</span>
                <input type="text" placeholder="Trustworthy source..." value=""></input>
                <select>
                    <option value="">Choose category:</option>
                    {CATEGORIES.map((cat)=> <option key={cat.name} value={cat.name}>{cat.name}</option >)}
                </select>
                <button class="btn2">Post</button></form>
        </div>    
  )

}

function Options(){
  return(
    <div className="options">
    <button className="option-btn">ALL</button>
    {CATEGORIES.map((cat)=> <button key={cat.name} className="option-btn">{cat.name}</button>)}
    </div>
  )
}

function Facts(){
    const facts =initialFacts
  return(
    <div className="facts">
    <ul className="facts-list">
    {facts.map((fact) => (
          <Fact key={fact.id} fact={fact}/>
    ))}
    </ul>
    </div>
  )
}

function Fact({fact}){

return(
        <li key = {fact.id}>
          {fact.text}
          <a className="fact-link" href={fact.source}>(Source)</a>                        
          <p style={{backgroundColor:CATEGORIES.find((cat)=>cat.name === fact.category).color}}>{fact.category}</p>
          <button><pre>üëç  0</pre></button>
          <button><pre>üòç  0</pre></button>
          <button><pre>‚õî  0</pre></button>
      </li>
)

}


export default App;
